mapper.select:
SELECT id,name,icon,url,type,pid FROM sys_menu
del_flag = 0
    and name like concat('%',#{name},'%')
    and pid = #{pId}

        and type = #{type}
        and type = 0



    limit #{limit},#{pageSize}

mapper.update:
UPDATE sys_menu
name = #{name}, icon = #{icon}, url = #{url}, type = #{type},
WHERE id = #{id}
